mapper.select:
select count(*)
from account
where ac_num=#{ac_num}

mapper.insert:
insert into sav_process values(sav_process_seq.nextval
	,(select a.ac_code ac_code
		from account a
			, account_client ac
	where a.ac_num=#{ac_num}
		 and ac.mem_code=#{mem_code}
		  and a.ac_code=ac.ac_code)
	,#{sp_name}
	,(select max(dep_code)
	            from deposit)
	,null
	,null
	,( select a.ac_balance ac_balance
			from account a
				, account_client ac
			where a.ac_num=#{ac_num}
				 and ac.mem_code=#{mem_code} 
				 and a.ac_code=ac.ac_code)
	+(select dep_money 
            from deposit 
            where dep_code=(select max(dep_code) 
                               from deposit))
)

mapper.select:
select count(*)
from account
where ac_num=#{ac_num}

select a.ac_balance ac_balance
from account a
	, account_client ac
where a.ac_num=#{ac_num}
	 and ac.mem_code=#{mem_code} 
	 and a.ac_code=ac.ac_code

mapper.insert:
  	insert into sav_process values(sav_process_seq.nextval
		,(select a.ac_code ac_code
		from account a
			, account_client ac
		where a.ac_num=#{ac_num}
			 and ac.mem_code=#{mem_code}
			  and a.ac_code=ac.ac_code )
		,#{sp_name}
		,null
		,(select max(WIT_CODE)
		            from withdraw)
		,null
		,(select a.ac_balance ac_balance
		 from account a
		 	, account_client ac
		where a.ac_num=#{ac_num}
			 and ac.mem_code=#{mem_code}
			  and a.ac_code=ac.ac_code)-(select wit_money 
					                       from withdraw
					                       where wit_code=(select max(wit_code) 
					                                          from withdraw))
	)

mapper.select:
	select count(*)
    from account
    where ac_num=#{ac_num}

	select a.ac_balance ac_balance
    from account a
    	, account_client ac
    where a.ac_num=#{ac_num}
    	 and ac.mem_code=#{mem_code} 
    	 and a.ac_code=ac.ac_code

mapper.insert:
		insert into sav_process values(sav_process_seq.nextval
        ,(select a.ac_code ac_code
        from account a
        	, account_client ac
        where a.ac_num=#{ac_num} 
        	and ac.mem_code=#{mem_code}
        	 and a.ac_code=ac.ac_code)
        ,#{sp_name}
        ,null
        ,(select max(WIT_CODE)
                    from withdraw)
        ,(select max(AT_CODE)
                    from transfer)
        ,(select a.ac_balance ac_balance
         from account a
         		,account_client ac
          where a.ac_num=#{ac_num}
          		 and ac.mem_code=#{mem_code}
          		  and a.ac_code=ac.ac_code)-(select wit_money 
								               from withdraw
								               where wit_code=(select max(wit_code) 
								                                  from withdraw))
        )

	insert into sav_process values(sav_process_seq.nextval
    ,(select a.ac_code ac_code
    from account a
    	, account_client ac
    where a.ac_num=#{ac_num}
    	 and ac.mem_code=#{mem_code}
    	  and a.ac_code=ac.ac_code)
    ,#{sp_name}
    ,(select max(dep_code)
                from deposit)
    ,null
    ,(select max(at_code)
                from transfer)
    ,(select a.ac_balance 
      from account a
      		, account_client ac 
      where a.ac_num=#{ac_num}
      		 and ac.mem_code=#{mem_code} 
      		 and a.ac_code=ac.ac_code)
    +(select dep_money 
           from deposit 
           where dep_code=(select max(dep_code) 
                              from deposit))
    )

mapper.select:
 	 select  nvl(mem_name,0) mem_name, nvl(mem_code,0) mem_code
		from(
		select ac.mem_code mem_code, m.mem_name mem_name
		from account a,member m,account_client ac
		where a.ac_num=#{ac_num} and ac.mem_code=m.mem_code and a.ac_code=ac.ac_code
		) right outer join dual on 1=1

mapper.insert:
insert into at_application
 values(at_application_seq.nextval
 		,(select a.ac_code 
 			from account a, account_client ac
 			 where ac.mem_code=#{mem_code}
 			 	 and a.ac_num=#{ac_num}
 			 	 and a.ac_code = ac.ac_code)
 		,#{ata_yourac}
 		,#{ata_setmoney}
 		,#{ata_startdate}
 		,#{ata_time}
 		,#{ata_stopdate}
 		,#{ata_dterm}
 		,#{ata_myacmemo}
 		,#{ata_youracmemo}
 		,#{ata_check}
 		,#{ata_ing}
 		,#{ata_yourmemcode}
 		,#{mem_code}

 		)
